import {
  WMSLegendData,
  TransformedLegendEntry,
  TaxonomyData,
  PostData
} from "@/types/types";

export async function transformLegendData(
  input: WMSLegendData
): Promise<TransformedLegendEntry[]> {
  const legendEntries =
    input?.Legend?.[0]?.rules?.[0]?.symbolizers?.[0]?.Raster?.colormap
      ?.entries;

  if (!legendEntries) {
    throw new Error('Invalid input format');
  }

  return legendEntries.map(({ label, color, opacity }) => ({
    label,
    color,
    opacity: parseFloat(opacity),
  }));
}

/**
 * Normalize options to a common format used by the RadioGroup component
 * @param options - The options to normalize
 */
export function normalizeOptions(
  options: Array<string | number | { label: string; value: string }>
): { label: string; value: string }[] {
  return options.map((item) => {
    if (typeof item === "string" || typeof item === "number") {
      // convert strings and numbers to { label, value } objects, all string for type safety
      return { label: String(item), value: String(item) };
    }

    // default to unmodified options, ie. label/value objects array
    return item;
  });
}

/**
 * Normalize props used by the RadioCard component
 * @param dataOrPromise - The data to normalize
 * @param type - The type of data to normalize, either 'taxonomy' or 'post'
 *
 */
export const normalizeRadioCardProps = async <T extends 'taxonomy' | 'post'>(
  dataOrPromise: (T extends 'taxonomy' ? TaxonomyData : PostData)[] | Promise<(T extends 'taxonomy' ? TaxonomyData : PostData)[]>,
  type: T
): Promise<T extends 'taxonomy' ? TaxonomyData[] : PostData[]> => {
  // make sure the data is resolved in case we received a promise
  const data = await dataOrPromise;

  const normalizedData =  await Promise.all(
    data?.map(async (item) => {
      // extract taxonomy data into a key:value structure (if available)
      if (type === 'taxonomy') {
        const taxonomyItem = item as TaxonomyData;

        return {
          id: taxonomyItem.id,
          name: taxonomyItem.name,
          slug: taxonomyItem.slug,
          link: taxonomyItem.link,
          value: taxonomyItem.slug,
          description: taxonomyItem.description || '',
          thumbnail: taxonomyItem?.thumbnail || null,
        } as TaxonomyData;
      }

      const postItem = item as PostData;
      const terms = postItem?._links['wp:term'] || [];

      const taxonomies = terms.reduce(
        // TODO: replace with correctly typed params
        (acc: Record<string, any[]>, term: { taxonomy: string }) => {
          if (term.taxonomy) {
            acc[term.taxonomy] = postItem[term.taxonomy] || [];
          }
          return acc;
        },
        {} as Record<string, any[]>
      );

      return {
        id: postItem.id,
        value: postItem.id,
        title: postItem.title,
        slug: postItem.slug || '',
        description: postItem.acf?.var_description || postItem.title,
        link: postItem.link || '',
        taxonomies,
        // TODO: do we want to use the post's featured_media image here? if so, we might need to make sure those have specific dimensions
        thumbnail: 'data:image/svg+xml;base64,' // temporary static thumbnail
      } as PostData;
    })
  );

  return normalizedData as T extends 'taxonomy' ? TaxonomyData[] : PostData[];
}